/**
 * Allgergens class.
 */
class NIW_Allergens {

	/**
	 * Returns the names of the products
	 *
	 * @return allergens_name
	 */
	public function show_allergens_name() {
		global $all_allergens;
		$allergens_name = array();

		foreach ( $all_allergens as $allergen ) {
			$allergens_name[ $allergen['key'] ] = $allergen['label'];
		}

		return $allergens_name;
	}

	/**
	 * Returns the svg of the products
	 *
	 * @return allergens_svg
	 */
	public function show_allergens_svg() {
		global $all_allergens;
		$allergens_svg = array();

		foreach ( $all_allergens as $key => $item ) {
			array_push( $allergens_svg, $item['svg'] );
		}

		return $allergens_svg;
	}

	/**
	 * Return the svg of the product by name
	 *
	 * @param string $name Name of SVG.
	 * @return html
	 */
	public function show_allergen_svg( $name ) {
		global $all_allergens;
		$allergen_svg = '';

		foreach ( $all_allergens as $key => $value ) {
			if ( $value['key'] == $name ) {
				$allergen_svg = $value['svg'];
			}
		}

		return $allergen_svg;
	}

	/**
	 * Return the svg of the product by name
	 */
	public function show_allergen_svg_vegan() {
		global $allergen_vegano;
		return $allergen_vegano['svg'];
	}

	/**
	 * Return the svg of the product by name
	 */
	public function show_allergen_name_vegan() {
		global $allergen_vegano;
		return $allergen_vegano['key'];
	}

}
